import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function FilmsList() {
    const [movies, setMovies] = useState([]);

    useEffect(() => {
        axios.get('https://api.themoviedb.org/3/movie/popular?api_key=793f529e214f3b041b733709431628c4')
            .then((res) => {
                setMovies(res.data.results);
            })
            .catch((err) => {
                console.log(err);
            });
    }, []);

    return (
        <>
            <h1 className="text-center mb-5 mt-5">Popular Movies</h1>
            <div className="container">
                <div className="row d-flex justify-content-between">
                    {movies.map((movie) => (
                        <div className="card col-3 mt-4" key={movie.id}>
                            <div className="movie-list" style={{ width: '18rem' }}>
                                <img
                                    className="card-img-top"
                                    src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                                    alt={movie.title}
                                />
                                <div className="card-body">
                                    <Link to={`/movie/${movie.id}`}>
                                        <h3 className="card-text text-center text-info">{movie.title}</h3>
                                    </Link>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
}

export default FilmsList;
