import React, { useState } from 'react';
import axios from './axiosConfig';

const MovieDetails = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = () => {
    const apiKey = 'YOUR_API_KEY';
    const apiUrl = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${searchQuery}`;

    axios.get(apiUrl)
      .then(response => {
        setSearchResults(response.data.results);
      })
      .catch(error => {
        console.error('Error searching movies:', error);
      });
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-5" style={{ color: "orange", border: "2px solid orange", padding: '10px', borderRadius: '10px' }}>Search Movies</h1>
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Search for movies..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button
          className="btn btn-primary"
          type="button"
          onClick={handleSearch}
        >
          Search
        </button>
      </div>
      <div className="row">
        {searchResults.map(movie => (
          <div key={movie.id} className="col-4 mb-4">
            {/* Your movie card component */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default MovieDetails;
