import React, { useState } from 'react';
import { Navbar, Container, Nav, NavDropdown, Form, FormControl, Button } from 'react-bootstrap';
import { useHistory } from 'react-router-dom';

function AppNavbar() {
  const [searchQuery, setSearchQuery] = useState('');
  const history = useHistory();

  const handleSearch = (e) => {
    e.preventDefault();
    // Redirect to the search results page with the entered query
    history.push(`/search?q=${searchQuery}`);
  };

  return (
    <Navbar expand="lg" style={{ backgroundColor: "gray" }}>
      <Container>
        <Navbar.Brand style={{ fontSize: "200%", color: "orange", fontStyle: "italic" }}> ðŸŽ¬  MOVIES</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarNavDropdown" />
        <Navbar.Collapse id="navbarNavDropdown">
          <Nav className="mr-auto">
            <Nav.Link to="home" active style={{ paddingLeft: "280%" }}>
              Home
            </Nav.Link>
            <Nav.Link to="#">Favourites</Nav.Link>

            <NavDropdown title="Dropdown link" id="navbarDropdownMenuLink">
              <NavDropdown.Item to="#">Login</NavDropdown.Item>
              <NavDropdown.Item to="#">Register</NavDropdown.Item>
            </NavDropdown>
          </Nav>
          <Form inline onSubmit={handleSearch}>
            <FormControl
              type="text"
              placeholder="Search"
              className="mr-sm-2"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <Button variant="outline-success" type="submit">Search</Button>
          </Form>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default AppNavbar;
