import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const PopularMovies = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const apiKey = '793f529e214f3b041b733709431628c4';
    const apiUrl = `https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}`;

    axios.get(apiUrl)
      .then(response => {
        setMovies(response.data.results);
      })
      .catch(error => {
        console.error('Error fetching popular movies:', error);
      });
  }, []);

  return (
    <>
      <h1 className="text-center mb-5 mt-5">Popular Movies</h1>
      <div className="container">
        <div className="row d-flex justify-content-between">
          {movies.map((movie) => (
            <div className="card col-3 mt-4" key={movie.id}>
              <div className="movie-list" style={{ width: '18rem' }}>
                <img
                  className="card-img-top"
                  src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                  alt={movie.title}
                />
                <div className="card-body">
                  <Link to={`/movie/${movie.id}`}>
                    <h3 className="card-text text-center text-info">{movie.title}</h3>
                  </Link>
                  <p className="card-text">Release Date: {movie.release_date}</p>
                  <p className="card-text">Vote Average: {movie.vote_average}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default PopularMovies;
