import React, { useEffect, useState } from 'react';
import axios from 'axios';

function PopularMovies() {
  const [movies, setMovies] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    const apiKey = 'YOUR_API_KEY';
    const apiUrl = `https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&page=${currentPage}`;

    axios.get(apiUrl)
      .then(response => {
        setMovies(response.data.results);
      })
      .catch(error => {
        console.error('Error fetching popular movies:', error);
      });
  }, [currentPage]);

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
    updateUrl(newPage);
  };

  const updateUrl = (newPage) => {
    const newUrl = new URL(window.location.href);
    newUrl.searchParams.set('page', newPage);
    window.history.pushState({}, '', newUrl);
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-5" style={{ color: "orange", border: "2px solid orange", padding: '10px', borderRadius: '10px' }}>Popular Movies</h1>
      <div className="row">
        {movies.map(movie => (
          <div key={movie.id} className="col-4 mb-4">
            {/* Your movie card component */}
          </div>
        ))}
      </div>
      <div className="text-center">
        <button
          className="btn btn-primary mr-2"
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 1}
        >
          Previous Page
        </button>
        <button
          className="btn btn-primary"
          onClick={() => handlePageChange(currentPage + 1)}
        >
          Next Page
        </button>
      </div>
    </div>
  );
}

export default PopularMovies;
