import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

function PopularMovies() {
  const [movies, setMovies] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  const apiKey = '793f529e214f3b041b733709431628c4';
  const apiUrl = `https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&page=${currentPage}`;

  useEffect(() => {
    axios.get(apiUrl)
      .then(response => {
        setMovies(response.data.results);
      })
      .catch(error => {
        console.error('Error fetching popular movies:', error);
      });
  }, [apiUrl]);

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-5" style={{ color: "orange", border: "2px solid orange", padding: '10px', borderRadius: '10px' }}>Popular Movies</h1>
      <div id="movieCarousel" className="carousel slide" data-bs-ride="carousel">
        <div className="carousel-inner row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
          {movies.map((movie, index) => (
            <div key={movie.id} className={`carousel-item${index === 0 ? ' active' : ''}`}>
              <Link to={`/movie/${movie.id}`} style={{ textDecoration: 'none' }}>
                <div className="card h-100">
                  <img
                    src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                    alt={movie.title}
                    className="card-img-top"
                    style={{ objectFit: 'cover' }}
                  />
                  <div className="card-body">
                    <h5 className="card-title">{movie.title}</h5>
                    <p className="card-text">{movie.overview}</p>
                  </div>
                  <div className="card-footer">
                    <Link to={`/movie/${movie.id}`} className="btn btn-primary" style={{ backgroundColor: 'orange', border: 'none', borderRadius: '5px' }}>
                      View Details
                    </Link>
                  </div>
                </div>
              </Link>
            </div>
          ))}
        </div>
        <button className="carousel-control-prev" type="button" data-bs-target="#movieCarousel" data-bs-slide="prev" onClick={() => handlePageChange(currentPage - 1)}>
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#movieCarousel" data-bs-slide="next" onClick={() => handlePageChange(currentPage + 1)}>
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Next</span>
        </button>
      </div>
    </div>
  );
}

export default PopularMovies;
