import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

function PopularMovies() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const apiKey = '793f529e214f3b041b733709431628c4';
    const apiUrl = `https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}`;

    axios.get(apiUrl)
      .then(response => {
        setMovies(response.data.results);
      })
      .catch(error => {
        console.error('Error fetching popular movies:', error);
      });
  }, []);

  // Function to shorten the movie overview
  const shortenOverview = (overview, maxLength) => {
    if (overview.length > maxLength) {
      return overview.substring(0, maxLength) + '...';
    }
    return overview;
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-5" style={{ color: "orange", border: "2px solid orange", padding: '10px', borderRadius: '10px' }}>Popular Movies</h1>
      <div className="row">
        {movies.map(movie => (
          <div key={movie.id} className="col-4 mb-4">
            <Link to={`/movie/${movie.id}`} style={{ textDecoration: 'none' }}>
              <div className="card" style={{ height: '70%', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', borderRadius: '10px', overflow: 'hidden' }}>
                <img
                  src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                  alt={movie.title}
                  className="card-img-top"
                  style={{ height: '50%', objectFit: 'cover' }}
                />
                <div className="card-body d-flex flex-column">
                  <h5 className="card-title" style={{ marginBottom: '10px' }}>{movie.title}</h5>
                  <p className="card-text" style={{ flex: 1, overflowY: 'auto', marginBottom: '20px' }}>
                    {shortenOverview(movie.overview, 150)}
                  </p>
                  <Link to={`/movie/${movie.id}`} className="btn btn-primary" style={{ backgroundColor: 'orange', border: 'none', borderRadius: '5px' }}>
                    Read More
                  </Link>
                </div>
              </div>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default PopularMovies;
