// src/components/AppNavbar.js
import React, { useState } from 'react';
import { Navbar, Container, Nav, NavDropdown, Form, FormControl, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faHeart } from '@fortawesome/free-solid-svg-icons';

function AppNavbar() {
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();
  const favorites = useSelector((state) => state.favorites.movies);

  const handleSearch = (e) => {
    e.preventDefault();
    navigate(`/search?q=${searchQuery}`);
  };

  return (
    <Navbar expand="lg" style={{ backgroundColor: 'gray' }}>
      <Container>
        <Navbar.Brand style={{ fontSize: '200%', color: 'orange', fontStyle: 'italic' }}>
          ðŸŽ¬ MOVIES
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarNavDropdown" />
        <Navbar.Collapse id="navbarNavDropdown">
          <Nav className="mr-auto">
            <Nav.Link to="home" active style={{ paddingLeft: '280%' }}>
              Home
            </Nav.Link>
            <Nav.Link to="/favorites">
              Favorites <span style={{ color: 'orange' }}>({favorites.length})</span>
            </Nav.Link>
            <NavDropdown title="Dropdown link" id="navbarDropdownMenuLink">
              <NavDropdown.Item to="#">Login</NavDropdown.Item>
              <NavDropdown.Item to="#">Register</NavDropdown.Item>
            </NavDropdown>
          </Nav>
          <Form inline onSubmit={handleSearch}>
            <FormControl
              type="text"
              placeholder="Search"
              className="mr-sm-2"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <Button
              variant="outline-success"
              type="submit"
              style={{ color: 'orange' }}
              className="search-button"
            >
              <FontAwesomeIcon
                icon={faSearch}
                style={{ color: 'orange', borderColor: 'orange', transition: 'color 0.3s' }}
                className="search-icon"
              />
            </Button>
          </Form>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default AppNavbar;
